题目传送门: 「Luogu P3254」圆桌问题 题目大意有$m$个单位,$n$个餐桌第$i$个单位的代表人数$r_i$, 第$i$张餐桌能容纳的人数$c_i$同一个单位的代表不能在同一个餐桌 若有一种方案,输出$1$,并输出方案若没有方案,输出$0$ 题解二分图,左点集为单位,右点集为餐桌,求其 最大匹配 对于每个单位,从 这个点 向 所有餐桌 接一条 容量为1 的边(即每个餐桌只能容纳同一个单位的一个人) 从 源点 向 每个单位 接一条 容量为单位人数 的边(即每个单位所有人都要参加) 从 每个餐桌 向 汇点 接一条 容量为餐桌人数 的边 如果最大流和所有单位总人数相等,则有可行方案对于每个单位,输出出边满足$\mathtt{e.cap == e.flow}$的$\mathtt{e.to-m}$ 代码123456789101112131415161718192021222324252627282930313233343536373839404142434445464748495051525354555657585960616263646566676869707172737475767778798081828384858687888990919293949596979899100101102103104105106107108109#include &lt;bits/stdc++.h&gt;using namespace std;inline int read() { int x = 0; int f = 1; char ch = getchar(); while (!isdigit(ch)) {if (ch == '-') f = -1; ch = getchar();} while (isdigit(ch)) {x = x * 10 + ch - 48; ch = getchar();} return x * f;}const int maxn = 450;const int inf = 0x3f3f3f3f;int n, m, s, t, d[maxn], cur[maxn];struct Edge { int from, to, cap, flow;};vector&lt;Edge&gt; edges;vector&lt;int&gt; G[maxn];void add(int from, int to, int cap) { edges.push_back((Edge) {from, to, cap, 0}); edges.push_back((Edge) {to, from, 0, 0}); int mm = edges.size(); G[from].push_back(mm - 2); G[to].push_back(mm - 1);}bool vis[maxn];bool BFS() { memset(vis, 0, sizeof(vis)); queue&lt;int&gt; Q; Q.push(s); d[s] = 0; vis[s] = 1; while (!Q.empty()) { int x = Q.front(); Q.pop(); for (int i = 0; i &lt; G[x].size(); ++i) { Edge&amp; e = edges[G[x][i]]; if (!vis[e.to] &amp;&amp; e.cap &gt; e.flow) { vis[e.to] = 1; d[e.to] = d[x] + 1; Q.push(e.to); } } } return vis[t];}int DFS(int x, int a) { if (x == t || a == 0) return a; int flow = 0, f; for (int&amp; i = cur[x]; i &lt; G[x].size(); ++i) { Edge&amp; e = edges[G[x][i]]; if (d[x] + 1 == d[e.to] &amp;&amp; (f = DFS(e.to, min(a, e.cap - e.flow))) &gt; 0) { e.flow += f; edges[G[x][i] ^ 1].flow -= f; flow += f; a -= f; if (a == 0) break; } } return flow;}int dinic(int s, int t) { int flow = 0; while (BFS()) { memset(cur, 0, sizeof(cur)); flow += DFS(s, inf); } return flow;}int tot;int main() { m = read(); n = read(); s = 0; t = m + n + 1; for (int i = 1; i &lt;= m; ++i) { for (int j = 1; j &lt;= n; ++j) { add(i, j + m, 1); } } for (int i = 1; i &lt;= m; ++i) { int r = read(); tot += r; add(s, i, r); } for (int i = 1; i &lt;= n; ++i) { int c = read(); add(i + m, t, c); } int maxflow = dinic(s, t); if (maxflow == tot) { printf(&quot;1
&quot;); for (int u = 1; u &lt;= m; ++u) { for (int i = 0; i &lt; G[u].size(); ++i) { Edge&amp; e = edges[G[u][i]]; if (e.to != s &amp;&amp; e.cap == e.flow) { printf(&quot;%d &quot;, e.to - m); } } printf(&quot;
&quot;); } } else { printf(&quot;0
&quot;); } return 0;}

题目传送门: 「Luogu P2770」航空路线问题 题目大意给出一个城市,$n$个点,$v$条边,每个城市有一个名字从西向东按序给出名字求从最西出发到达最东并返回最西(除起点外,每个城市只能访问一次)的路径 题解将每个城市拆成入点和出点 源点为点1的入点,汇点为点n的出点 对于 除源点汇点 的每个点, 从 入点 向 出点 建一条 容量为1,费用为1 的边(只能经过一次,且对答案贡献为1) 从 点1的入点 向 点1的出点 建一条 容量为2,费用为1 的边(可以经过2次,且对答案贡献为1) 从 点n的入点 向 点n的出点 建一条 容量为2,费用为1 的边(可以经过2次,且对答案贡献为1) 对于边$&lt;u, v&gt;$, 从 u的出点 向 v的入点 建一条 容量为1,费用为0 的边(可经过1次,对答案无贡献) 求 最大费用最大流, 最大流$maxflow\leq 2$ 若最大流为$2$, 则会有一条道路, 经过的城市数为最大费用$maxcost-2$(减去重复的源点和汇点的贡献)求路径可以 先一次dfs找到从1到n的所有残量为0的路径(满流$\mathtt{e.cap == e.flow}$),正序输出 再一次dfs找到另一条满流路径,并用vis确保没有重复城市,倒序输出(不重复输出n) 若最大流为$1$, 则直接从源点到汇点有一条通路, 输出$2$, 路径为$1-&gt;n-&gt;1$ 若最大流为$0$, 则无解$	exttt{No Solution!}$ 代码123456789101112131415161718192021222324252627282930313233343536373839404142434445464748495051525354555657585960616263646566676869707172737475767778798081828384858687888990919293949596979899100101102103104105106107108109110111112113114115116117118119120121122123124125126127128129130131#include &lt;bits/stdc++.h&gt;using namespace std;inline int read() { int x = 0; int f = 1; char ch = getchar(); while (!isdigit(ch)) {if (ch == '-') f = -1; ch = getchar();} while (isdigit(ch)) {x = x * 10 + ch - 48; ch = getchar();} return x * f;}const int maxn = 210;const int inf = 0x3f3f3f3f;const int ninf = 0xc0c0c0c0;int n, m, s, t, ansflow;int vis[maxn], d[maxn], p[maxn], a[maxn];long long anscost;struct Edge { int from, to, cap, flow, cost; Edge(int u, int v, int c, int f, int w): from(u), to(v), cap(c), flow(f), cost(w){}};vector&lt;Edge&gt; edges;vector&lt;int&gt; G[maxn];void add(int u, int v, int c, int w) { edges.push_back(Edge(u, v, c, 0, w)); edges.push_back(Edge(v, u, 0, 0,-w)); int mm = edges.size(); G[u].push_back(mm - 2); G[v].push_back(mm - 1);}bool BellmanFord(int&amp; flow, long long&amp; cost) { memset(d, 0xc0, sizeof(d)); memset(vis, 0, sizeof(vis)); d[s] = 0; vis[s] = 1; p[s] = 0; a[s] = inf; queue&lt;int&gt; Q; Q.push(s); while (!Q.empty()) { int x = Q.front(); Q.pop(); vis[x] = 0; for (int i = 0; i &lt; G[x].size(); ++i) { Edge&amp; e = edges[G[x][i]]; if (e.cap &gt; e.flow &amp;&amp; d[e.to] &lt; d[x] + e.cost) { d[e.to] = d[x] + e.cost; p[e.to] = G[x][i]; a[e.to] = min(a[x], e.cap - e.flow); if (!vis[e.to]) { Q.push(e.to); vis[e.to] = 1; } } } } if (d[t] == ninf) return false; flow += a[t]; cost += (long long)d[t] * (long long)a[t]; for (int u = t; u != s; u = edges[p[u]].from) { edges[p[u]].flow += a[t]; edges[p[u] ^ 1].flow -= a[t]; } return true;}int MinCostMaxFlow(long long&amp; cost) { int flow = 0; cost = 0; while (BellmanFord(flow, cost)); return flow;}string name[maxn];map&lt;string, int&gt; id;void dfs1(int u) { vis[u] = true; cout &lt;&lt; name[u - n] &lt;&lt; endl; for (int i = 0; i &lt; G[u].size(); ++i) { Edge&amp; e = edges[G[u][i]]; if (e.to &lt;= n &amp;&amp; e.cap == e.flow) { dfs1(e.to + n); break; } }}void dfs2(int u) { for (int i = 0; i &lt; G[u].size(); ++i) { Edge&amp; e = edges[G[u][i]]; if (e.to &lt;= n &amp;&amp; e.cap == e.flow &amp;&amp; !vis[e.to + n]) { dfs2(e.to + n); } } cout &lt;&lt; name[u - n] &lt;&lt; endl;}int main() { n = read(); m = read(); s = 1; t = 2 * n; for (int i = 1; i &lt;= n; ++i) { cin &gt;&gt; name[i]; id[name[i]] = i; } for (int i = 1; i &lt;= m; ++i) { string str1, str2; cin &gt;&gt; str1; cin &gt;&gt; str2; int u = id[str1], v = id[str2]; if (u &gt; v) swap(u, v); add(u + n, v, 1, 0); } for (int i = 2; i &lt; n; ++i) { add(i, i + n, 1, 1); } add(s, s + n, 2, 1); add(n, t, 2, 1); int maxflow = MinCostMaxFlow(anscost); if (maxflow == 2) { printf(&quot;%lld
&quot;, anscost - 2); } else if (maxflow == 1) { printf(&quot;2
&quot;); cout &lt;&lt; name[1] &lt;&lt; &quot;
&quot; &lt;&lt; name[n] &lt;&lt; &quot;
&quot; &lt;&lt; name[1] &lt;&lt; endl; return 0; } else { printf(&quot;No Solution!
&quot;); return 0; } memset(vis, 0, sizeof(vis)); dfs1(1 + n); dfs2(1 + n); return 0;}

题目传送门: 「Luogu P2762」太空飞行计划问题 题目大意有$m$个实验，每个实验只可以进行一次，但会获得相应的奖金有$n$个仪器，每个实验都需要一定的仪器，每个仪器可以运用于多个实验，但需要一定的价值 问奖金与代价的差的最大值是多少？ 题解 从源点向每个实验建 容量为实验奖金 的边 从每个仪器向汇点建 容量为仪器费用 的边 实验向对应仪器建 容量为$inf$ 的边(保证不被割) 然后求最小割即最大流,只会割断 源-&gt;实验 或者 仪器-&gt;汇 的点最后一次bfs后,仍和源相连的实验/仪器为要进行的实验/需要的仪器 最大收益为所有支付的费用之和减最大流 样例的建边见下图: 代码123456789101112131415161718192021222324252627282930313233343536373839404142434445464748495051525354555657585960616263646566676869707172737475767778798081828384858687888990919293949596979899100101102103104105106107108109110111112113114115#include &lt;bits/stdc++.h&gt;using namespace std;bool endr = false;inline int read() { int x = 0; int f = 1; char ch = getchar(); while (!isdigit(ch)) {if (ch == '-') f = -1; ch = getchar();} while (isdigit(ch)) {x = x * 10 + ch - 48; ch = getchar();} if (ch == '') endr = true; return x * f;}const int maxn = 110;const int inf = 0x3f3f3f3f;int n, m, s, t, d[maxn], cur[maxn], tot;struct Edge { int from, to, cap, flow;};vector&lt;Edge&gt; edges;vector&lt;int&gt; G[maxn];void add(int from, int to, int cap) { edges.push_back((Edge) {from, to, cap, 0}); edges.push_back((Edge) {to, from, 0, 0}); int mm = edges.size(); G[from].push_back(mm - 2); G[to].push_back(mm - 1);}bool vis[maxn];bool BFS() { memset(vis, 0, sizeof(vis)); queue&lt;int&gt; Q; Q.push(s); d[s] = 0; vis[s] = 1; while (!Q.empty()) { int x = Q.front(); Q.pop(); for (int i = 0; i &lt; G[x].size(); ++i) { Edge&amp; e = edges[G[x][i]]; if (!vis[e.to] &amp;&amp; e.cap &gt; e.flow) { vis[e.to] = 1; d[e.to] = d[x] + 1; Q.push(e.to); } } } return vis[t];}int DFS(int x, int a) { if (x == t || a == 0) return a; int flow = 0, f; for (int&amp; i = cur[x]; i &lt; G[x].size(); ++i) { Edge&amp; e = edges[G[x][i]]; if (d[x] + 1 == d[e.to] &amp;&amp; (f = DFS(e.to, min(a, e.cap - e.flow))) &gt; 0) { e.flow += f; edges[G[x][i] ^ 1].flow -= f; flow += f; a -= f; if (a == 0) break; } } return flow;}int dinic(int s, int t) { int flow = 0; while (BFS()) { memset(cur, 0, sizeof(cur)); flow += DFS(s, inf); } return flow;}int main() { int m_ = read(), n_ = read(); n = m_ + n_ + 2; s = 0; t = n - 1; for (int i = 1; i &lt;= m_; ++i) { int p = read(); tot += p; add(s, i, p); char tools[10000]; memset(tools, 0, sizeof(tools)); cin.getline(tools, 10000); int ulen = 0, tool; while (sscanf(tools + ulen, &quot;%d&quot;, &amp;tool) == 1) { add(i, tool + m_, inf); if (tool == 0) ulen++; else { while (tool) { tool /= 10; ulen++; } } ulen++; } } for (int i = 1; i &lt;= n_; ++i) { int c = read(); add(i + m_, t, c); } int ans = dinic(s, t); for (int i = 1; i &lt;= m_; ++i) { if (vis[i]) printf(&quot;%d &quot;, i); } printf(&quot;
&quot;); for (int i = 1; i &lt;= n_; ++i) { if (vis[i + m_]) printf(&quot;%d &quot;, i); } printf(&quot;
&quot;); printf(&quot;%d
&quot;, tot - ans); return 0;}
